----------------------------------------------------------  DDL for Table ESB_CANONICAL_ERROR--------------------------------------------------------
  
  CREATE TABLE "ESB_CANONICAL_ERROR"    (	"ID" NUMBER NOT NULL, 	"CODE" VARCHAR2(50 BYTE) NOT NULL, 	"TYPE_ID" NUMBER NOT NULL, 	"DESCRIPTION" VARCHAR2(255 BYTE), 	"PRIORITY" VARCHAR2(50 BYTE) NOT NULL,	"RCD_STATUS" NUMBER(1,0) DEFAULT 1 NOT NULL, 	"CREATIONDATE" DATE DEFAULT CURRENT_DATE, 	 CONSTRAINT "ESB_CANONICAL_ERROR_PK" PRIMARY KEY ("ID"),  	 CONSTRAINT "ESB_CANONICAL_ERROR_TYPE_FK" FOREIGN KEY ("TYPE_ID") REFERENCES "ESB_CANONICAL_ERROR_TYPE" ("ID"),	 CONSTRAINT "ESB_CANONICAL_ERROR_STATUS_CK" CHECK ("RCD_STATUS" IN(0,1))   );   COMMENT ON COLUMN "ESB_CANONICAL_ERROR"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';