----------------------------------------------------------  DDL for Table ESB_CANONICAL_ERROR_TYPE--------------------------------------------------------    CREATE TABLE "ESB_CANONICAL_ERROR_TYPE"    (	"ID" NUMBER NOT NULL, 	"TYPE" VARCHAR2(50 BYTE) NOT NULL, 	"DESCRIPTION" VARCHAR2(255 BYTE), 	"RCD_STATUS" NUMBER(1,0) DEFAULT 1 NOT NULL, 	"CREATIONDATE" DATE DEFAULT CURRENT_DATE, 	 CONSTRAINT "ESB_CANONICAL_ERROR_TYPE_PK" PRIMARY KEY ("ID"),	 CONSTRAINT "ESB_CAN_ERR_TYPE_STATUS_CK" CHECK ("RCD_STATUS" IN(0,1))   );   COMMENT ON COLUMN "ESB_CANONICAL_ERROR_TYPE"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';