----------------------------------------------------------  DDL for Table ESB_CAPABILITY--------------------------------------------------------
    CREATE TABLE "ESB_CAPABILITY"    (		"ID" NUMBER NOT NULL, 	"SERVICE_ID" NUMBER NOT NULL, 	"CODE" VARCHAR2(20 BYTE) NOT NULL, 	"NAME" VARCHAR2(20 BYTE) NOT NULL, 	"RCD_STATUS" NUMBER(1,0) DEFAULT 1 NOT NULL,	"CREATIONDATE" DATE DEFAULT CURRENT_DATE,	 CONSTRAINT "ESB_CAPABILITY_PK" PRIMARY KEY ("ID"),	 CONSTRAINT "ESB_CAPABILITY_FK" FOREIGN KEY ("SERVICE_ID") REFERENCES "ESB_SERVICE" ("ID"),	 CONSTRAINT "ESB_CAPABILITY_STATUS_CK" CHECK ("RCD_STATUS" IN(0,1))  );  COMMENT ON COLUMN "ESB_CAPABILITY"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';