----------------------------------------------------------  DDL for Table ESB_CAPABILITY_CHECK--------------------------------------------------------
      CREATE TABLE "ESB_CAPABILITY_CHECK"    (		"ID" NUMBER NOT NULL, 	"CAPABILITY_ID" NUMBER NOT NULL, 	"CHECK_INDICATOR" NUMBER NOT NULL, 	"RCD_STATUS" NUMBER(1,0) NOT NULL, 	"CREATIONDATE" DATE DEFAULT CURRENT_DATE, 	 CONSTRAINT "ESB_CAPABILITY_CHECK_PK" PRIMARY KEY ("ID"),	 CONSTRAINT "ESB_CAPABILITY_CHECK_STATUS_CK" CHECK ("RCD_STATUS" IN(0,1))   );   COMMENT ON COLUMN "ESB_CAPABILITY_CHECK"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';