----------------------------------------------------------  DDL for Table CAPABILITY--------------------------------------------------------    CREATE TABLE "ESB_CAPABILITY_DETAILS_TYPE"    (	"ID" NUMBER NOT NULL, 	"NAME" VARCHAR2(50 BYTE), 	"DESCRIPTION" VARCHAR2(200 BYTE), 	"RCD_STATUS" NUMBER(1,0) DEFAULT 1 NOT NULL, 	"CREATIONDATE" DATE DEFAULT CURRENT_DATE,	 CONSTRAINT "ESB_CAPABILITY_DETAILS_TYPE" PRIMARY KEY ("ID")   );   COMMENT ON COLUMN "ESB_CAPABILITY_DETAILS_TYPE"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';