----------------------------------------------------------  DDL for Table ESB_CDM_ENTITY--------------------------------------------------------
    CREATE TABLE "ESB_CDM_ENTITY"    (		"ID" NUMBER NOT NULL, 	"NAME" VARCHAR2(50 BYTE) NOT NULL, 	"MAJ_VERSION" NUMBER NOT NULL, 	"DESCRIPTION" VARCHAR2(100 BYTE), 	"RCD_STATUS" NUMBER(1,0),	"CREATIONDATE" DATE DEFAULT CURRENT_DATE,	 CONSTRAINT "ESB_CDM_ENTITY_PK" PRIMARY KEY ("ID"),	 CONSTRAINT "ESB_CDM_ENTITY_STATUS_CK" CHECK ("RCD_STATUS" IN(0,1))   );   COMMENT ON COLUMN "ESB_CDM_ENTITY"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';