----------------------------------------------------------  DDL for Table ESB_CDM_FIELD--------------------------------------------------------
  CREATE TABLE "ESB_CDM_FIELD"    (	"ID" NUMBER NOT NULL, 	"ENTITY_ID" NUMBER NOT NULL, 	"NAME" VARCHAR2(50 BYTE) NOT NULL, 	"DESCRIPTION" VARCHAR2(100 BYTE), 	"RCD_STATUS" NUMBER(1,0) NOT NULL,	"CREATIONDATE" DATE DEFAULT CURRENT_DATE,		 CONSTRAINT "ESB_CDM_FIELD_PK" PRIMARY KEY ("ID"),	 CONSTRAINT "ESB_CDM_FIELD_ENTITY_FK" FOREIGN KEY ("ENTITY_ID")	  REFERENCES "ESB_CDM_ENTITY" ("ID"),	 CONSTRAINT "ESB_CDM_FIELD_STATUS_CK" CHECK ("RCD_STATUS" IN(0,1))   );   COMMENT ON COLUMN "ESB_CDM_FIELD"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';