----------------------------------------------------------  DDL for Table ESB_CHECK_CONFIG--------------------------------------------------------  CREATE TABLE "ESB_CHECK_CONFIG"    (	"CAP_CHK_ID" NUMBER NOT NULL, 	"CHECK_TYPE_ID" NUMBER NOT NULL, 	"ELAPSED_TIME" INTERVAL DAY (2) TO SECOND (0) NOT NULL, 	"RCD_STATUS" NUMBER(1,0) NOT NULL, 	"CREATIONDATE" DATE DEFAULT CURRENT_DATE, 	 CONSTRAINT "ESB_CHECK_CONFIG_PK" PRIMARY KEY ("CAP_CHK_ID", "CHECK_TYPE_ID"),	 CONSTRAINT "ESB_CHECK_CONFIG_CHK_FK" FOREIGN KEY ("CAP_CHK_ID")	  REFERENCES "ESB_CAPABILITY_CHECK" ("ID"), 	 CONSTRAINT "ESB_CHECK_CONFIG_TYPE_FK" FOREIGN KEY ("CHECK_TYPE_ID")	  REFERENCES "ESB_CHECK_TYPE" ("ID"),	 CONSTRAINT "ESB_CHECK_CONFIG_STATUS_CK" CHECK ("RCD_STATUS" IN(0,1))   );   COMMENT ON COLUMN "ESB_CHECK_CONFIG"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';