----------------------------------------------------------  DDL for Table ESB_CHECK_TYPE--------------------------------------------------------

  CREATE TABLE "ESB_CHECK_TYPE"    (	"ID" NUMBER NOT NULL, 	"NAME" VARCHAR2(100 BYTE) NOT NULL, 	"GRADE" NUMBER NOT NULL, 	"RCD_STATUS" NUMBER(1,0) NOT NULL, 	"CREATIONDATE" DATE DEFAULT CURRENT_DATE, 	 CONSTRAINT "ESB_CHECK_TYPE_PK" PRIMARY KEY ("ID"),	 CONSTRAINT "ESB_CHECK_TYPE_STATUS_CK" CHECK ("RCD_STATUS" IN(0,1))   );   COMMENT ON COLUMN "ESB_CHECK_TYPE"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';