----------------------------------------------------------  DDL for Table ESB_CONFIG--------------------------------------------------------    CREATE TABLE "ESB_CONFIG"    (	"ID" NUMBER NOT NULL, 	"NAME" VARCHAR2(100 BYTE) NOT NULL, 	"RCD_STATUS" NUMBER(1,0) DEFAULT 1 NOT NULL, 	"CREATIONDATE" DATE DEFAULT CURRENT_DATE,	 CONSTRAINT "ESB_CONFIG_PK" PRIMARY KEY ("ID"),   CONSTRAINT "ESB_CONFIG_UK" UNIQUE ("NAME"),	 CONSTRAINT "ESB_CONFIG_STATUS_CK" CHECK ("RCD_STATUS" IN(0,1))   );   COMMENT ON COLUMN "ESB_CONFIG"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';