----------------------------------------------------------  DDL for Table ESB_CONFIG_PROPERTY--------------------------------------------------------
  
  CREATE TABLE "ESB_CONFIG_PROPERTY"    (	"ID" NUMBER NOT NULL, 	"NAME" VARCHAR2(30 BYTE) NOT NULL, 	"DESCRIPTION" VARCHAR2(50 BYTE), 	"RCD_STATUS" NUMBER(1,0) DEFAULT 1 NOT NULL, 	"CREATIONDATE" DATE DEFAULT CURRENT_DATE,	 CONSTRAINT "ESB_CONFIG_PROPERTY_PK" PRIMARY KEY ("ID"),	 CONSTRAINT "ESB_CONFIG_PROPERTY_STATUS_CK" CHECK ("RCD_STATUS" IN(0,1))   );   COMMENT ON COLUMN "ESB_CONFIG_PROPERTY"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';