----------------------------------------------------------  DDL for Table ESB_CONSUMER_DETAILS--------------------------------------------------------    CREATE TABLE "ESB_CONSUMER_DETAILS"    (	"ID" NUMBER NOT NULL, 	"CONSUMER_ID" NUMBER NOT NULL, 	"DETAIL_TYPE_ID" NUMBER NOT NULL, 	"DETAIL_CONTENT" CLOB, 	"RCD_STATUS" NUMBER(1,0) DEFAULT 1 NOT NULL, 	"CREATIONDATE" DATE DEFAULT CURRENT_DATE,	 CONSTRAINT "ESB_CONSUMER_DETAILS" PRIMARY KEY ("ID"),	 CONSTRAINT "ESB_CONSUMER_DETAILS_FK" FOREIGN KEY ("CONSUMER_ID")			REFERENCES "ESB_CONSUMER" ("ID"), 	 CONSTRAINT "ESB_ERROR_STATUS_TYPE_FK2" FOREIGN KEY ("DETAIL_TYPE_ID")			REFERENCES "ESB_CONSUMER_DETAILS_TYPE" ("ID")   );   COMMENT ON COLUMN "ESB_CONSUMER_DETAILS"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';