----------------------------------------------------------  DDL for Table ESB_CONVERSATION--------------------------------------------------------   CREATE TABLE "ESB_CONVERSATION"    (	"ID" NUMBER NOT NULL, 	"MESSAGE_TX_ID" NUMBER NOT NULL, 	"CAPABILITY_ID" NUMBER NOT NULL,	"CONVERSATION_ID" VARCHAR2(50 BYTE) NOT NULL, 	"CORRELATION_ID" VARCHAR2(50 BYTE), 	"CHANNEL_ID" NUMBER NOT NULL, 	"CONSUMER_ID" NUMBER NOT NULL, 	"COUNTRY_ID" NUMBER NOT NULL, 	"SEQUENCE" NUMBER DEFAULT 0, 	"RCD_STATUS" NUMBER(1,0) DEFAULT 1 NOT NULL, 	"CREATIONDATE" DATE DEFAULT CURRENT_DATE, 	 CONSTRAINT "ESB_CONVERSATION_PK" PRIMARY KEY ("ID"),  	 CONSTRAINT "ESB_CONVERSATION_MSGTX_FK" FOREIGN KEY ("MESSAGE_TX_ID")	  REFERENCES "ESB_MESSAGE_TRANSACTION" ("ID") ON DELETE CASCADE, 	 CONSTRAINT "ESB_CONVERSATION_COUNTRY_FK" FOREIGN KEY ("COUNTRY_ID")	  REFERENCES "ESB_COUNTRY" ("ID"), 	 CONSTRAINT "ESB_CONVERSATION_CHANNEL_FK" FOREIGN KEY ("CHANNEL_ID")	  REFERENCES "ESB_CHANNEL" ("ID"), 	 CONSTRAINT "ESB_CONVERSATION_CONSUMER_FK" FOREIGN KEY ("CONSUMER_ID")	  REFERENCES "ESB_CONSUMER" ("ID"), 	 CONSTRAINT "ESB_CONVERSATION_CAPABILITY_FK" FOREIGN KEY ("CAPABILITY_ID")	  REFERENCES "ESB_CAPABILITY" ("ID")   );   COMMENT ON COLUMN "ESB_CONVERSATION"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';