----------------------------------------------------------  DDL for Table ESB_COUNTRY--------------------------------------------------------
  
  CREATE TABLE "ESB_COUNTRY"    (	"ID" NUMBER NOT NULL, 	"CODE" VARCHAR2(3 BYTE), 	"NAME" VARCHAR2(50 BYTE) NOT NULL, 	"DESCRIPTION" VARCHAR2(30 BYTE), 	"RCD_STATUS" NUMBER(1,0) DEFAULT 1 NOT NULL, 	"CREATIONDATE" DATE DEFAULT CURRENT_DATE,	 CONSTRAINT "ESB_COUNTRY_PK" PRIMARY KEY ("ID"),	 CONSTRAINT "ESB_COUNTRY_STATUS_CK" CHECK ("RCD_STATUS" IN(0,1))   );   COMMENT ON COLUMN "ESB_COUNTRY"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';