----------------------------------------------------------  DDL for Table ESB_DEFAULT_CHECK--------------------------------------------------------    CREATE TABLE "ESB_DEFAULT_CHECK"    (	"ID" NUMBER NOT NULL, 	"TYPE" NUMBER(1,0) NOT NULL, 	"NAME" VARCHAR2(100 BYTE) NOT NULL, 	"ELAPSED_TIME" INTERVAL DAY (2) TO SECOND (6),	"GRADE" NUMBER NOT NULL, 	"RCD_STATUS" NUMBER(1,0) NOT NULL, 	"CREATIONDATE" DATE DEFAULT CURRENT_DATE, 	 CONSTRAINT "ESB_DEFAULT_CHECK_PK" PRIMARY KEY ("ID"),	 CONSTRAINT "ESB_DEFAULT_CHECK_STATUS_CK" CHECK ("RCD_STATUS" IN(0,1)),	 CONSTRAINT "ESB_DEFAULT_CHECK_TYPE_CK" CHECK ("RCD_STATUS" IN(1,2))   );   COMMENT ON COLUMN "ESB_DEFAULT_CHECK"."TYPE" IS '1=INITIAL, 2=FINAL';   COMMENT ON COLUMN "ESB_DEFAULT_CHECK"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';