----------------------------------------------------------  DDL for Table ESB_ENTERPRISE--------------------------------------------------------
  
  CREATE TABLE "ESB_ENTERPRISE"    (	"ID" NUMBER NOT NULL, 	"CODE" VARCHAR2(20 BYTE) NOT NULL, 	"NAME" VARCHAR2(20 BYTE) NOT NULL, 	"RCD_STATUS" NUMBER(1,0) DEFAULT 1 NOT NULL, 	"CREATIONDATE" DATE DEFAULT CURRENT_DATE,	 CONSTRAINT "ESB_ENTERPRISE_PK" PRIMARY KEY ("ID"),	 CONSTRAINT "ESB_ENTERPRISE_STATUS_CK" CHECK ("RCD_STATUS" IN(0,1))   );   COMMENT ON COLUMN "ESB_ENTERPRISE"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';