----------------------------------------------------------  DDL for Table ESB_ERROR--------------------------------------------------------    CREATE TABLE "ESB_ERROR"    (	"ID" NUMBER NOT NULL, 	"TRACE_ID" NUMBER NOT NULL, 	"STATUS_ID" NUMBER NOT NULL, 	"DESCRIPTION" VARCHAR2(255 BYTE), 	"CAN_ERR_ID" NUMBER, 	"RAW_FAULT" CLOB, 	"RAW_CODE" VARCHAR2(20 BYTE), 	"RAW_DESCRIPTION" VARCHAR2(255 BYTE), 	"ERROR_SOURCE" NUMBER NOT NULL, 	"ERROR_SOURCE_DETAILS" VARCHAR2(255 BYTE), 	"MODULE" VARCHAR2(50 BYTE), 	"SUB_MODULE" VARCHAR2(50 BYTE), 	"RCD_STATUS" NUMBER(1,0) DEFAULT 1 NOT NULL, 	"CREATIONDATE" DATE DEFAULT CURRENT_DATE, 	 CONSTRAINT "ESB_ERROR_PK" PRIMARY KEY ("ID"),	 CONSTRAINT "ESB_ERROR_STATUS_CK" CHECK ("RCD_STATUS" IN(0,1)),	 CONSTRAINT "ESB_ERROR_TRACE_FK" FOREIGN KEY ("TRACE_ID")			REFERENCES "ESB_TRACE" ("ID") ON DELETE CASCADE, 	 CONSTRAINT "ESB_ERROR_STATUS_TYPE_FK" FOREIGN KEY ("STATUS_ID")			REFERENCES "ESB_ERROR_STATUS_TYPE" ("ID"), 	 CONSTRAINT "ESB_ERROR_CAN_FK" FOREIGN KEY ("CAN_ERR_ID")			REFERENCES "ESB_CANONICAL_ERROR" ("ID"),	 CONSTRAINT "ESB_SYSTEM_FK" FOREIGN KEY ("ERROR_SOURCE")			REFERENCES "ESB_SYSTEM" ("ID")   );   COMMENT ON COLUMN "ESB_ERROR"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';