----------------------------------------------------------  DDL for Table ESB_ERROR_HOSPITAL--------------------------------------------------------    CREATE TABLE "ESB_ERROR_HOSPITAL"    (	"ID" NUMBER NOT NULL, 	"MESSAGE_TX_ID" NUMBER NOT NULL, 	"THERAPY_ID" NUMBER NULL, 	"STATUS" VARCHAR2(20 BYTE) NOT NULL, 	"RCD_STATUS" NUMBER(1,0) DEFAULT 1 NOT NULL, 	"DESCRIPTION" VARCHAR2(200 BYTE), 	"CREATIONDATE" DATE DEFAULT CURRENT_DATE,	CONSTRAINT "ESB_ERROR_HOSPITAL_PK" PRIMARY KEY ("ID"),	CONSTRAINT "ESB_ERROR_HOSPITAL_CHK_MSG_FK" FOREIGN KEY ("MESSAGE_TX_ID")    REFERENCES "ESB_MESSAGE_TRANSACTION" ("ID"),    CONSTRAINT "ESB_ERROR_HOSPITAL_CHK_THER_FK" FOREIGN KEY ("THERAPY_ID")    REFERENCES "ESB_ERROR_THERAPY" ("ID"),	CONSTRAINT "ESB_ERROR_HOSPITAL_CHECK_CK1" CHECK ("RCD_STATUS" IN(0,1))   );   COMMENT ON COLUMN "ESB_ERROR_HOSPITAL"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';