----------------------------------------------------------  DDL for Table ESB_ERROR_THERAPY--------------------------------------------------------    CREATE TABLE "ESB_ERROR_THERAPY"    (	"ID" NUMBER NOT NULL, 	"NAME" VARCHAR2(100 BYTE) NOT NULL, 	"RCD_STATUS" NUMBER(1,0) DEFAULT 1 NOT NULL, 	"CREATIONDATE" DATE DEFAULT CURRENT_DATE,	CONSTRAINT "ESB_ERROR_THERAPY_PK" PRIMARY KEY ("ID"),	CONSTRAINT "ESB_ERROR_THERAPY_STATUS_CK" CHECK ("RCD_STATUS" IN(0,1))   );   COMMENT ON COLUMN "ESB_ERROR_THERAPY"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';