----------------------------------------------------------  DDL for Table ESB_ERROR_TREATMENT_PLAN--------------------------------------------------------    CREATE TABLE "ESB_ERROR_TREATMENT_PLAN"    (	"ID" NUMBER NOT NULL, 	"THERAPY_ID" NUMBER NOT NULL, 	"TREATMENT_ID" NUMBER NOT NULL, 	"RCD_STATUS" NUMBER(1,0) DEFAULT 1 NOT NULL, 	"CREATIONDATE" DATE DEFAULT CURRENT_DATE,	CONSTRAINT "ESB_ERROR_T_PLAN_PK" PRIMARY KEY ("ID"),	CONSTRAINT "ESB_ERROR_T_PLAN_CHK_THERAP_FK" FOREIGN KEY ("THERAPY_ID")    REFERENCES "ESB_ERROR_THERAPY" ("ID"),    CONSTRAINT "ESB_ERROR_T_PLAN_CHK_T_FK" FOREIGN KEY ("TREATMENT_ID")    REFERENCES "ESB_ERROR_TREATMENT_TYPE" ("ID"),	CONSTRAINT "ESB_ERROR_T_PLAN_STATUS_CK" CHECK ("RCD_STATUS" IN(0,1))   );   COMMENT ON COLUMN "ESB_ERROR_TREATMENT_PLAN"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';