----------------------------------------------------------  DDL for Table ESB_LOG--------------------------------------------------------    CREATE TABLE "ESB_LOG"    (	"ID" NUMBER NOT NULL, 	"TRACE_ID" NUMBER NOT NULL, 	"MESSAGE" CLOB, 	"TYPE_ID" NUMBER NOT NULL, 	"SEVERITY_ID" NUMBER NOT NULL, 	"DESCRIPTION" VARCHAR2(255 BYTE), 	"RCD_STATUS" NUMBER(1,0) DEFAULT 1 NOT NULL, 	"CREATIONDATE" DATE DEFAULT CURRENT_DATE, 	 CONSTRAINT "ESB_LOG_PK" PRIMARY KEY ("ID"),  	 CONSTRAINT "ESB_LOG_TRACE_FK" FOREIGN KEY ("TRACE_ID")	  REFERENCES "ESB_TRACE" ("ID") ON DELETE CASCADE, 	 CONSTRAINT "ESB_LOG_TYPE_FK" FOREIGN KEY ("TYPE_ID")	  REFERENCES "ESB_LOG_TYPE" ("ID"), 	 CONSTRAINT "ESB_SEVERITY_FK" FOREIGN KEY ("SEVERITY_ID")	  REFERENCES "ESB_SEVERITY" ("ID"),	 CONSTRAINT "ESB_LOG_STATUS_CK" CHECK ("RCD_STATUS" IN(0,1))   );   COMMENT ON COLUMN "ESB_LOG"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';