----------------------------------------------------------  DDL for Table ESB_MAPPING--------------------------------------------------------    CREATE TABLE "ESB_MAPPING"    (	"ID" NUMBER NOT NULL,    	"SOURCE_SYSTEM" NUMBER NOT NULL, 	"CONTEXT" VARCHAR2(100 BYTE),	"SOURCE_CODE" VARCHAR2(250 BYTE) NOT NULL, 	"DESTINATION_SYSTEM" NUMBER NOT NULL, 	"DESTINATION_CODE" VARCHAR2(250 BYTE), 	"FIELD_ID" NUMBER, 	"RCD_STATUS" NUMBER(1,0) DEFAULT 1 NOT NULL, 	"CREATIONDATE" DATE DEFAULT CURRENT_DATE,	 CONSTRAINT "ESB_MAPPING_PK" PRIMARY KEY ("ID"),  	 CONSTRAINT "ESB_MAPPING_UK1" UNIQUE ("SOURCE_SYSTEM", "SOURCE_CODE", "DESTINATION_SYSTEM", "CONTEXT", "FIELD_ID"),  	 CONSTRAINT "SOURCE_MAPPING_SYSTEM_FK" FOREIGN KEY ("SOURCE_SYSTEM")	  REFERENCES "ESB_SYSTEM" ("ID"), 	 CONSTRAINT "DESTINATION_MAPPING_SYSTEM_FK" FOREIGN KEY ("DESTINATION_SYSTEM")	  REFERENCES "ESB_SYSTEM" ("ID"), 	 CONSTRAINT "ESB_MAPPING_CDM_FIELD_FK" FOREIGN KEY ("FIELD_ID")	  REFERENCES "ESB_CDM_FIELD" ("ID"),	 CONSTRAINT "ESB_MAPPING_STATUS_CK" CHECK ("RCD_STATUS" IN(0,1))   );   COMMENT ON COLUMN "ESB_MAPPING"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';