---------------------------------------------------------  DDL for Table ESB_MESSAGE_TRANSACTION-------------------------------------------------------    CREATE TABLE "ESB_MESSAGE_TRANSACTION"    (	"ID" NUMBER NOT NULL, 	"PROC_ID" VARCHAR2(50 BYTE), 	"EVENT_ID" VARCHAR2(50 BYTE) NOT NULL, 	"STATUS" VARCHAR2(20 BYTE), 	"RCD_STATUS" NUMBER(1,0) DEFAULT 1 NOT NULL, 	"CREATIONDATE" DATE DEFAULT CURRENT_DATE, 	"CLIENT_REQ_TIMESTAMP" TIMESTAMP (6), 	"MSG_TIMESTAMP" TIMESTAMP (6), 	"SEQUENCE" NUMBER DEFAULT 0 NOT NULL, 	 CONSTRAINT "ESB_MESSAGE_TRANSACTION_PK" PRIMARY KEY ("ID"),	 CONSTRAINT "ESB_MESSAGE_TRANSACTION_CK1" CHECK ("RCD_STATUS" IN(0,1))   );   COMMENT ON COLUMN "ESB_MESSAGE_TRANSACTION"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';