----------------------------------------------------------  DDL for Table ESB_PARAMETER--------------------------------------------------------
  
  CREATE TABLE "ESB_PARAMETER"    (	"KEY" VARCHAR2(100 BYTE) NOT NULL, 	"VALUE" VARCHAR2(255 BYTE), 	"RCD_STATUS" NUMBER(1,0) DEFAULT 1 NOT NULL, 	"CREATIONDATE" DATE DEFAULT CURRENT_DATE,	 CONSTRAINT "ESB_PARAMETER_PK" PRIMARY KEY ("KEY"),	 CONSTRAINT "ESB_PARAMETER_STATUS_CK" CHECK ("RCD_STATUS" IN(0,1))   );   COMMENT ON COLUMN "ESB_PARAMETER"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';