----------------------------------------------------------  DDL for Table ESB_RETRYMANAGER--------------------------------------------------------    CREATE TABLE "ESB_RETRYMANAGER"    (	"ID" NUMBER NOT NULL, 	"RETRYMANAGER_CONFIG_ID" NUMBER NOT NULL, 	"ERROR_HOSPITAL_ID" NUMBER NULL, 	"CONV_ORIGINAL_ID" VARCHAR2(100 BYTE) NOT NULL, 	"RETRY_COUNT" NUMBER NULL, 	"STATUS" VARCHAR2(20 BYTE) NOT NULL, 	"DATE_NEXT_RETRY" TIMESTAMP, 	"ERROR_TREATMENT_ID" NUMBER NULL, 	"REQUEST_BODY" CLOB, 	"CREATIONDATE" DATE DEFAULT CURRENT_DATE,	CONSTRAINT "ESB_RETRYMANAGER_PK" PRIMARY KEY ("ID"),	CONSTRAINT "ESB_RETRYMANAGER_RM_FK" FOREIGN KEY ("RETRYMANAGER_CONFIG_ID")    REFERENCES "ESB_RETRYMANAGER_CONFIG" ("ID"),    CONSTRAINT "ESB_RETRYMANAGER_EH_FK" FOREIGN KEY ("ERROR_HOSPITAL_ID")    REFERENCES "ESB_ERROR_HOSPITAL" ("ID")   );   