----------------------------------------------------------  DDL for Table ESB_RETRYMANAGER_CONFIG--------------------------------------------------------    CREATE TABLE "ESB_RETRYMANAGER_CONFIG"    (	"ID" NUMBER NOT NULL, 	"CAPABILITY_ID" NUMBER NOT NULL, 	"RETRY_MAX" NUMBER NOT NULL, 	"INTERVAL_TIME" NUMBER NOT NULL, 	"RETRY_ENABLED" NUMBER(1,0) DEFAULT 1 NOT NULL, 	"CREATIONDATE" DATE DEFAULT CURRENT_DATE,	CONSTRAINT "ESB_RETRYMANAGER_HISTORY_PK" PRIMARY KEY ("ID")   );   COMMENT ON COLUMN "ESB_RETRYMANAGER_CONFIG"."RETRY_ENABLED" IS '1=ACTIVE, 0=INACTIVE';