----------------------------------------------------------  DDL for Table ESB_SERVICE--------------------------------------------------------
  
  CREATE TABLE "ESB_SERVICE"    (	"ID" NUMBER NOT NULL, 	"CODE" VARCHAR2(20 BYTE) NOT NULL, 	"NAME" VARCHAR2(50 BYTE) NOT NULL, 	"RCD_STATUS" NUMBER(1,0) DEFAULT 1 NOT NULL, 	"CREATIONDATE" DATE DEFAULT CURRENT_DATE,	 CONSTRAINT "ESB_SERVICE_PK" PRIMARY KEY ("ID"),	 CONSTRAINT "ESB_SERVICE_STATUS_CK" CHECK ("RCD_STATUS" IN(0,1))   );   COMMENT ON COLUMN "ESB_SERVICE"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';