----------------------------------------------------------  DDL for Table ESB_CONSUMER_DETAILS--------------------------------------------------------    CREATE TABLE "ESB_SERVICE_DETAILS"    (	"ID" NUMBER NOT NULL, 	"SERVICE_ID" NUMBER NOT NULL, 	"DETAIL_TYPE_ID" NUMBER NOT NULL, 	"DETAIL_CONTENT" CLOB, 	"RCD_STATUS" NUMBER(1,0) DEFAULT 1 NOT NULL, 	"CREATIONDATE" DATE DEFAULT CURRENT_DATE,	 CONSTRAINT "ESB_SERVICE_DETAILS" PRIMARY KEY ("ID"),	 CONSTRAINT "ESB_SERVICE_DETAILS_FK" FOREIGN KEY ("SERVICE_ID")			REFERENCES "ESB_SERVICE" ("ID"), 	 CONSTRAINT "ESB_SERVICE_TYPE_FK" FOREIGN KEY ("DETAIL_TYPE_ID")			REFERENCES "ESB_SERVICE_DETAILS_TYPE" ("ID")   );   COMMENT ON COLUMN "ESB_SERVICE_DETAILS"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';