----------------------------------------------------------  DDL for Table ESB_SEVERITY--------------------------------------------------------
  
  CREATE TABLE "ESB_SEVERITY"    (	"ID" NUMBER NOT NULL, 	"LEVEL" VARCHAR2(50 BYTE) NOT NULL, 	"DESCRIPTION" VARCHAR2(255 BYTE), 	"ORDER" NUMBER NOT NULL, 	"RCD_STATUS" NUMBER(1,0) DEFAULT 1 NOT NULL, 	"CREATIONDATE" DATE DEFAULT CURRENT_DATE, 	 CONSTRAINT "ESB_SEVERITY_PK" PRIMARY KEY ("ID"),	 CONSTRAINT "ESB_SEVERITY_STATUS_CK" CHECK ("RCD_STATUS" IN(0,1))   );   COMMENT ON COLUMN "ESB_SEVERITY"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';