----------------------------------------------------------  DDL for Table ESB_SYSTEM--------------------------------------------------------    CREATE TABLE "ESB_SYSTEM"    (	"ID" NUMBER NOT NULL, 	"CODE" VARCHAR2(40 BYTE) NOT NULL, 	"NAME" VARCHAR2(50 BYTE), 	"DESCRIPTION" VARCHAR2(100 BYTE), 	"RCD_STATUS" NUMBER(1,0) DEFAULT 1 NOT NULL, 	"CREATIONDATE" DATE DEFAULT CURRENT_DATE, 	 CONSTRAINT "ESB_SYSTEM_PK" PRIMARY KEY ("ID"),	 CONSTRAINT "ESB_SYSTEM_UNIQUE" UNIQUE ("CODE", "NAME"),	 CONSTRAINT "ESB_SYSTEM_STATUS_CK" CHECK ("RCD_STATUS" IN(0,1))	    );   COMMENT ON COLUMN "ESB_SYSTEM"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';