----------------------------------------------------------  DDL for Table ESB_TRACE--------------------------------------------------------    CREATE TABLE "ESB_TRACE"    (	"ID" NUMBER NOT NULL, 	"CONV_ID" NUMBER NOT NULL, 	"SEQUENCE" NUMBER DEFAULT 0, 	"COMPONENT" VARCHAR2(100 BYTE), 	"OPERATION" VARCHAR2(100 BYTE), 	"LOG_TIMESTAMP" TIMESTAMP (9) NOT NULL, 	"LOG_PLACEHOLDER" VARCHAR2(100 BYTE),	"RCD_STATUS" NUMBER(1,0) DEFAULT 1 NOT NULL, 	"CREATIONDATE" DATE DEFAULT CURRENT_DATE, 	 CONSTRAINT "ESB_TRACE_PK" PRIMARY KEY ("ID"),  	 CONSTRAINT "ESB_TRACE_CONVERSATION_FK" FOREIGN KEY ("CONV_ID")	  REFERENCES "ESB_CONVERSATION" ("ID") ON DELETE CASCADE,	 CONSTRAINT "ESB_TRACE_STATUS_CK" CHECK ("RCD_STATUS" IN(0,1))   );   COMMENT ON COLUMN "ESB_TRACE"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';