----------------------------------------------------------  DDL for Table ESB_TRANSACTION_CHECK--------------------------------------------------------    CREATE TABLE "ESB_TRANSACTION_CHECK"    (	"ID" NUMBER NOT NULL, 	"MESSAGE_TX_ID" NUMBER NOT NULL, 	"CONVERSATION_ID" VARCHAR2(50) NOT NULL, 	"CAPABILITY_ID" NUMBER NOT NULL,	"RSP_MSG" CLOB, 	"RESULT_CODE" VARCHAR2(10 BYTE), 	"RESULT_DESC" VARCHAR2(100 BYTE), 	"RCD_STATUS" NUMBER(1,0) NOT NULL, 	"CREATIONDATE" DATE DEFAULT CURRENT_DATE, 	 CONSTRAINT "ESB_TRANSACTION_CHECK_PK" PRIMARY KEY ("ID"),  	 CONSTRAINT "ESB_TRANSACTION_CHK_MSG_FK" FOREIGN KEY ("MESSAGE_TX_ID")	  REFERENCES "ESB_MESSAGE_TRANSACTION" ("ID") ON DELETE CASCADE,	 CONSTRAINT "ESB_TRANSACTION_CHECK_FK1" FOREIGN KEY ("CAPABILITY_ID")	  REFERENCES "ESB_CAPABILITY" ("ID"), 	 CONSTRAINT "ESB_TRANSACTION_CHECK_CK1" CHECK ("RCD_STATUS" IN(0,1))   );   COMMENT ON COLUMN "ESB_TRANSACTION_CHECK"."RCD_STATUS" IS '1=ACTIVE, 0=INACTIVE';